import React from 'react';
import { connect } from 'react-redux';
import styled from 'styled-components';

const PaymentSuccess = ({ totalGuest,
  startDay,
  endDay,
  stayDate,
  totalprice,
  eachHotelValues,}) => {
  return (
    <Div>
      <Container>
        {/* <Confetti width={width} height={height} numberOfPieces={450} /> */}
        <Title>Booking is Confirmed!</Title>
        <Message>Hrer is your Booking details</Message>
        <div>
            <p className="bold">Dates</p>
            <p>
              {startDay} - {endDay}
            </p>
          </div>
          <div>
            <p className="bold">Check-in-time</p>
            <p>03:00pm-08:00pm</p>
          </div>
          <div>
            <p className="bold">Guests</p>
            <p>{totalGuest} guest</p>
          </div>
          <div className='summary'>
              <p>{address}</p>
              <p>{title}</p>
              <p>{option1}</p>
              <p className="bold">{rating}</p>
            </div>
      </Container>
    </Div>
  );
};

const Div= styled.div`
border: 1px solid #dddddd;
    border-radius: 20px;
    padding: 2rem;
`
const Container = styled.div`
  width: 475px;
  margin: 30px auto 0 auto;
  text-align: center;
`;

const Title = styled.div`
  font-size: 58px;
`;

const Message = styled.div`
  margin-top: 40px;
`;

const mapStateToProps = ({
  booking: {
    guest,
    adult,
    infant,
    startDay,
    endDay,
    stayDate,
    totalprice,
    eachHotelValues,
  },
}) => ({
  totalGuest: guest + adult + infant,
  startDay,
  endDay,
  stayDate,
  totalprice,
  eachHotelValues,
});
export default connect(mapStateToProps)(PaymentSuccess) ;
