import React, { useState } from 'react';
import styled from 'styled-components';
import { IoHeartOutline, IoHeartSharp } from 'react-icons/io5';
import Imgslider from './Imgslider';
import { connect } from 'react-redux';

const HotelInfo = ({ data, stayDate }) => {
  const [isHeart, setIsHeart] = useState(true);
  const clickHeart = () => {
    setIsHeart(!isHeart);
  };
  const { id, address, fee, imgUrl, option1, option2, title } = data;
  console.log(data);
  console.log(stayDate, fee);
  console.log(stayDate * fee);
  console.log(typeof stayDate);
  console.log(typeof fee);
  const total = stayDate * fee;
  console.log(total);
  return (
    <>
      <Bar />
      <Form id={id}>
        <ImgSection>
          <Imgslider img={imgUrl} />
        </ImgSection>
        <TextSection>
          <AddressInfo>{address} 의 집 전체 </AddressInfo>
          <Title>{title} </Title>
          <Bar width="2vw" margin="1vw 0 0.5vw 0" />
          <Info1>{option1}</Info1>
          <Info2>{option2}</Info2>
          <HeartBtn onClick={clickHeart}>
            {isHeart ? (
              <IoHeartOutline style={{ fontSize: '2vw' }} />
            ) : (
              <IoHeartSharp style={{ fontSize: '2vw' }} />
            )}
          </HeartBtn>
          <Price>${fee}/박</Price>
          <TotalPrice>총액 $ {total}</TotalPrice>
        </TextSection>
      </Form>
    </>
  );
};

const Form = styled.div`
  position: relative;
  width: 100%;
  height: 15vw;
  ${({ theme }) => theme.flexSet('flex-start', 'flex-start')}
`;
const Bar = styled.div`
  width: ${props => props.width || '100%'};
  height: 1px;
  margin: ${props => props.margin || '2vw 0'};
  background-color: #d3d3d3;
`;
const ImgSection = styled.span`
  margin: 0 1vw;
  width: 15vw;
  height: 100%;
`;

const TextSection = styled.span`
  display: flex;
  flex-direction: column;
`;
const AddressInfo = styled.span`
  margin-bottom: 1vw;
  font-size: 1.4rem;
  line-height: 18px;
  align-items: center;
  width: 100%;
  color: #c0c0c0;
`;
const Title = styled.span`
  width: 23vw;
`;
const Info1 = styled.span`
  margin-bottom: 1vw;
  font-size: 1.2rem;
  line-height: 18px;
  align-items: center;
  width: 100%;
  color: #c0c0c0;
`;
const Info2 = styled.span`
  font-size: 1rem;
  line-height: 18px;
  align-items: center;
  width: 100%;
  color: #c0c0c0;
`;
const HeartBtn = styled.span`
  position: absolute;
  right: 1vw;
  cursor: pointer;
  z-index: 10;
`;
const Price = styled.span`
  position: absolute;
  right: 1vw;
  bottom: 2vw;
  cursor: pointer;
`;
const TotalPrice = styled.span`
  position: absolute;
  right: 1vw;
  bottom: 0;
  cursor: pointer;
  font-size: 14px;
  line-height: 18px;
  align-items: center;
  color: #c0c0c0;
`;
const mapStateToProps = ({ booking: { stayDate } }) => ({
  stayDate,
});

// const mapDispatchToProps = (dispatch)=>({
//   getTotalValue:
// })
export default connect(mapStateToProps, null)(HotelInfo);
